ReentrantLock实现思路：
import java.util.concurrent.ConcurrentLinkedQueue;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.concurrent.locks.Condition;
import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.LockSupport;

public class MyReentrantLock implements Lock {

    /**
     * 锁的重入次数
     */
    private final AtomicInteger count = new AtomicInteger();
    /**
     * 当前持有锁的线程
     */
    private volatile Thread owner;

    /**
     * 相当于 synchronized 监视器锁中的 waitSet
     */
    private final ConcurrentLinkedQueue waiters = new ConcurrentLinkedQueue<>();

    @Override
    public void lock() {
        waiters.offer(Thread.currentThread());
        for (; ; ) {
            Thread thread = waiters.peek();
            if (thread != Thread.currentThread()) {
                LockSupport.park();
                continue;
            }
            boolean succ = tryLock();
            if (!succ) {
                LockSupport.park();
                continue;
            }
            waiters.poll();
            return;
        }
    }

    @Override
    public void lockInterruptibly() throws InterruptedException {

    }

    @Override
    public boolean tryLock() {
        int ct = count.get();
        if (ct == 0) {
            // 锁没被占用,抢锁
            boolean success = count.compareAndSet(ct, ct + 1);
            if (success) {
                // 抢到了
                owner = Thread.currentThread();
            }
            return success;
        } else {
            // 锁被占用
            if (owner == Thread.currentThread()) {
                // 锁被当前线程占用，重入次数++
                count.set(ct + 1);
                return true;
            } else {
                // 锁被其他线程占用
                return false;
            }
        }
    }

    @Override
    public boolean tryLock(long time, TimeUnit unit) throws InterruptedException {
        return false;
    }

    @Override
    public void unlock() {
        if (!tryUnlock()) {
            return;
        }
        Thread thread = waiters.peek();
        if (thread == null) {
            return;
        }
        LockSupport.unpark(thread);
    }

    /**
     * @return 重入次数彻底归零，释放掉锁
     */
    private boolean tryUnlock() {
        if (owner != Thread.currentThread()) {
            throw new IllegalMonitorStateException();
        }
        int ct = count.get();
        int newCt = ct - 1;
        count.set(newCt);
        if (newCt == 0) {
            owner = null;
            return true;
        }
        return false;
    }

    @Override
    public Condition newCondition() {
        return null;
    }
}

