import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.ReadWriteLock;
import java.util.concurrent.locks.ReentrantReadWriteLock;

public class MyHashMap {

    private final Map m = new HashMap<>();

    private final ReadWriteLock rwl = new ReentrantReadWriteLock();
    private final Lock r = rwl.readLock();
    private final Lock w = rwl.writeLock();


    public V get(K k) {
        r.lock();
        try {
            return m.get(k);
        } finally {
            r.unlock();
        }
    }

    public K[] allKeys() {
        r.lock();
        try {
            return (K[]) m.keySet().toArray();
        } finally {
            r.unlock();
        }
    }

    public V put(K k, V v) {
        w.lock();
        try {
            return m.put(k, v);
        } finally {
            w.unlock();
        }
    }

    public void clear() {
        w.lock();
        try {
            m.clear();
        } finally {
            w.unlock();
        }
    }

}

读写锁解决缓存雪崩的问题
class TeacherInfoCache {
    static volatile boolean cacheValid;
    static final ReadWriteLock rwl = new ReentrantReadWriteLock();

    static Object get(String dataKey) {
        Object data = null;
        rwl.readLock().lock();
        try {
            if (cacheValid) {
                data = Redis.data.get(dataKey);
            } else {
                // 可能会发生缓存雪崩的问题(刚开始缓存不可用，很多线程查库)
                data = DataBase.queryUserInfo();
                Redis.data.put(dataKey, data);
                cacheValid = true;
            }
            return data;
        } finally {
            rwl.readLock().unlock();
        }
    }

    // 查库时加个写锁,并且双重检查
    static Object get2(String dataKey) {
        if (!cacheValid) {
            rwl.writeLock().lock();
            try {
                if (!cacheValid) {
                    Object data = DataBase.queryUserInfo();
                    Redis.data.put(dataKey, data);
                    cacheValid = true;
                    return data;
                }
            } finally {
                rwl.writeLock().unlock();
            }
        }
        rwl.readLock().lock();
        try {
            return Redis.data.get(dataKey);
        } finally {
            rwl.readLock().unlock();
        }
    }
}

class DataBase {
    static String queryUserInfo() {
        System.out.println("查询数据库。。。");
        return "name:Kody,age:40,gender:true";
    }
}

