手写FutureTask
import java.util.concurrent.*;
import java.util.concurrent.atomic.AtomicReference;
import java.util.concurrent.locks.LockSupport;

public class MyFutureTask implements Runnable, Future {

    // FutureTask 只能执行一次
    private volatile int state = NEW;

    private static final int NEW = 0;
    private static final int RUNNING = 1;
    private static final int FINISH = 2;

    private final AtomicReference runner = new AtomicReference<>(null);

    /**
     * 执行结果
     */
    private V result;

    private final Callable callable;

    // 等待执行结果的等待队列
    private final LinkedBlockingQueue waiters = new LinkedBlockingQueue<>(100);

    public MyFutureTask(Callable callable) {
        this.callable = callable;
    }

    @Override
    public void run() {
        if (state != NEW) {
            return;
        }
        if (!runner.compareAndSet(null, Thread.currentThread())) {
            return;
        }
        state = RUNNING;
        try {
            result = callable.call();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            state = FINISH;
        }
        for (; ; ) {
            Thread waiter = waiters.poll();
            if (waiter == null) {
                break;
            }
            LockSupport.unpark(waiter);
        }

    }

    @Override
    public V get() throws InterruptedException, ExecutionException {
        if (state != FINISH) {
            waiters.offer(Thread.currentThread());
        }
        while (state != FINISH) {
            LockSupport.park();
        }
        return result;
    }

    @Override
    public boolean cancel(boolean mayInterruptIfRunning) {
        return false;
    }

    @Override
    public boolean isCancelled() {
        return false;
    }

    @Override
    public boolean isDone() {
        return false;
    }


    @Override
    public V get(long timeout, TimeUnit unit) throws InterruptedException, ExecutionException, TimeoutException {
        return null;
    }


    public static void main(String[] args) {
        MyFutureTask futureTask = new MyFutureTask<>(new Callable() {
            @Override
            public String call() throws Exception {
                LockSupport.parkNanos(1000 * 1000 * 1000 * 5L);
                System.out.println("done ..."); // 两次启动线程执行，但这行只输出一次
                return "Finish";
            }
        });

        // 第1次执行
        new Thread(futureTask).start();
        try {
            String s = futureTask.get();
            System.out.println(s);
        } catch (InterruptedException e) {
            e.printStackTrace();
        } catch (ExecutionException e) {
            e.printStackTrace();
        }

        // 第2次执行
        new Thread(futureTask).start();
        try {
            String s = futureTask.get();
            System.out.println(s);
        } catch (InterruptedException e) {
            e.printStackTrace();
        } catch (ExecutionException e) {
            e.printStackTrace();
        }
    }
}

